class Prices extends React.Component{
    state = {
        currency : 'USD'
    }

    render () {
        let list =  <li className="list-group-item">
        Bitcoin rate for {this.props.bpi.USD.description} : <span className="badge badge-primary">{this.props.bpi.USD.code}</span>
        <strong>{this.props.bpi.USD.rate}</strong>
    </li>;

        if(this.state.currency  === 'USD'){
            let list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.USD.description} : <span className="badge badge-primary">{this.props.bpi.USD.code}</span>
            <strong>{this.props.bpi.USD.rate}</strong>
        </li>;
        }
        else if(this.state.currency  === 'GBP'){
            let list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.GBP.description} : <span className="badge badge-primary">{this.props.bpi.GBP.code}</span>
            <strong>{this.props.bpi.GBP.rate}</strong>
        </li>;
        }
        else if(this.state.currency  === 'EUR'){
            let list =  <li className="list-group-item">
            Bitcoin rate for {this.props.bpi.EUR.description} : <span className="badge badge-primary">{this.props.bpi.EUR.code}</span>
            <strong>{this.props.bpi.EUR.rate}</strong>
        </li>;
        }

        return(
            <div>
               <ul className="list-group">

                {list}

               </ul>
            </div>
        );
    }
}

export default Prices;
